INSERT INTO public.lessons (title, description, category, difficulty, duration_minutes, content, scenario_text, is_published, order_index) VALUES
('AI Ethics and Responsible Use', 'Understand ethical considerations and responsible practices when working with AI systems in professional environments.', 'ai_literacy', 'intermediate', 5, '{"sections": [{"title": "Core Ethical Principles", "bullets": ["Transparency in AI decision-making", "Fairness and bias prevention", "Privacy and data protection", "Accountability for AI outcomes"], "content": "Ethical AI use requires understanding both capabilities and limitations."}, {"title": "Practical Guidelines", "bullets": ["Always verify AI-generated content", "Understand data sources and training", "Consider impact on stakeholders", "Document AI usage in critical decisions"], "content": "Apply ethical frameworks to daily AI interactions."}]}', 'Your team wants to use AI to screen job applications. What ethical considerations should guide this decision?', true, 19),
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'AI Ethics and Responsible Use'), 'What is a key ethical principle when using AI for hiring decisions?', '["Maximize speed of decisions", "Ensure transparency and fairness", "Minimize human involvement", "Prioritize cost reduction"]', 1, 'Transparency and fairness are fundamental to ethical AI use, especially in high-impact decisions like hiring that affect people''s livelihoods.', 1),
((SELECT id FROM lessons WHERE title = 'AI Ethics and Responsible Use'), 'When should you document AI usage in decision-making?', '["Only for legal compliance", "Never, to protect IP", "In critical decisions affecting people", "Only when AI makes errors"]', 2, 'Critical decisions that impact stakeholders should document AI involvement to maintain accountability and enable auditing.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Prompt Engineering Fundamentals'), 'What makes a prompt more effective?', '["Being vague to allow creativity", "Providing specific context and examples", "Using technical jargon", "Keeping it as short as possible"]', 1, 'Specific context and examples help the AI understand exactly what you need, leading to better, more relevant outputs.', 1),
((SELECT id FROM lessons WHERE title = 'Prompt Engineering Fundamentals'), 'What is "few-shot learning" in prompt engineering?', '["Using very short prompts", "Providing examples in your prompt", "Limiting AI responses", "Using multiple AI tools"]', 1, 'Few-shot learning involves providing examples in your prompt to demonstrate the pattern or format you want the AI to follow.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'AI Tools for Productivity'), 'Which task is most suitable for AI automation?', '["Strategic planning", "Meeting transcription", "Building client relationships", "Making ethical judgments"]', 1, 'Meeting transcription is a routine, time-consuming task that AI can handle effectively, freeing humans for higher-value work.', 1),
((SELECT id FROM lessons WHERE title = 'AI Tools for Productivity'), 'How should you approach implementing AI productivity tools?', '["Replace all manual processes immediately", "Start with repetitive, time-consuming tasks", "Only use in non-critical areas", "Avoid due to security risks"]', 1, 'Starting with repetitive, time-consuming tasks allows you to gain value quickly while learning to use AI tools effectively.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Understanding AI Limitations'), 'What is an "AI hallucination"?', '["AI refusing to respond", "AI generating false information confidently", "AI being too creative", "AI working too slowly"]', 1, 'AI hallucinations occur when models generate false or fabricated information while presenting it as factual, requiring human verification.', 1),
((SELECT id FROM lessons WHERE title = 'Understanding AI Limitations'), 'When is AI least appropriate to use?', '["Data analysis", "Draft email writing", "High-stakes medical diagnoses", "Meeting summaries"]', 2, 'High-stakes medical diagnoses require specialized human expertise, empathy, and accountability that AI cannot provide.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'AI in Decision Making'), 'How should AI recommendations be used in decision-making?', '["Implement them directly", "As one input among many", "Only when they agree with intuition", "Ignore them to avoid bias"]', 1, 'AI should provide data-driven insights as one input, combined with human judgment, experience, and contextual understanding.', 1),
((SELECT id FROM lessons WHERE title = 'AI in Decision Making'), 'What should you do before acting on AI recommendations?', '["Get stakeholder approval", "Validate assumptions and test edge cases", "Implement immediately for speed", "Get legal clearance"]', 1, 'Testing assumptions and edge cases helps identify potential failures before AI recommendations impact real outcomes.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'AI and Data Privacy'), 'What is a key privacy risk when using public AI tools?', '["Slow processing", "High cost", "Data leakage to training sets", "Limited features"]', 2, 'Public AI tools may use your inputs for training, potentially exposing sensitive information to others or the model itself.', 1),
((SELECT id FROM lessons WHERE title = 'AI and Data Privacy'), 'How should you handle sensitive data with AI?', '["Never use AI with sensitive data", "Anonymize before processing", "Only use during business hours", "Get written consent first"]', 1, 'Anonymizing sensitive information before AI processing protects privacy while still allowing you to benefit from AI capabilities.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Evaluating AI Output Quality'), 'How do you verify AI-generated content?', '["Trust it if it sounds confident", "Cross-reference with authoritative sources", "Use multiple AI tools", "Check grammar and spelling"]', 1, 'Cross-referencing with authoritative sources is the most reliable way to validate AI output accuracy.', 1),
((SELECT id FROM lessons WHERE title = 'Evaluating AI Output Quality'), 'What indicates potential AI output problems?', '["Technical terminology", "Inconsistency across similar queries", "Detailed responses", "Fast generation"]', 1, 'Inconsistent responses to similar queries suggest the AI may be unreliable or hallucinating information.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'AI Collaboration Strategies'), 'What is an effective AI collaboration approach?', '["Give one detailed prompt and accept the result", "Iterate with feedback to refine results", "Use the same prompt for everything", "Never revise AI output"]', 1, 'Iterative feedback helps the AI understand your needs better and produces higher quality results over time.', 1),
((SELECT id FROM lessons WHERE title = 'AI Collaboration Strategies'), 'How should teams standardize AI use?', '["Everyone uses different tools", "Document and share effective patterns", "Restrict AI to one person", "Avoid documentation"]', 1, 'Documenting and sharing successful patterns helps the entire team benefit from AI more effectively and consistently.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Future of AI in the Workplace'), 'What skills become more valuable as AI advances?', '["Routine data entry", "Creative problem-solving", "Memorization", "Following scripts exactly"]', 1, 'Creative problem-solving, empathy, and strategic thinking are uniquely human skills that complement rather than compete with AI.', 1),
((SELECT id FROM lessons WHERE title = 'Future of AI in the Workplace'), 'How should professionals prepare for AI evolution?', '["Resist adoption", "Develop AI literacy continuously", "Focus only on current tools", "Avoid experimentation"]', 1, 'Continuous learning and experimentation with AI tools keeps professionals competitive and adaptable as capabilities evolve.', 2);
INSERT INTO public.lessons (title, description, category, difficulty, duration_minutes, content, scenario_text, is_published, order_index) VALUES
('Understanding Regulatory Frameworks', 'Learn the fundamentals of regulatory compliance and how frameworks apply to your industry.', 'compliance_workflows', 'beginner', 5, '{"sections": [{"title": "Core Compliance Concepts", "bullets": ["Know your industry regulations", "Understand enforcement mechanisms", "Identify compliance obligations", "Map regulations to business processes"], "content": "Effective compliance starts with understanding applicable regulations."}, {"title": "Framework Structure", "bullets": ["Federal vs state requirements", "Industry-specific regulations", "International standards", "Internal policies vs external mandates"], "content": "Different frameworks require different approaches and priorities."}]}', 'Your company is expanding into a new state with different regulations. How do you ensure compliance?', true, 28),
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Understanding Regulatory Frameworks'), 'What is the first step in compliance?', '["Hiring lawyers", "Understanding applicable regulations", "Creating policies", "Training employees"]', 1, 'Understanding which regulations apply to your business is the foundation for all other compliance activities.', 1),
((SELECT id FROM lessons WHERE title = 'Understanding Regulatory Frameworks'), 'How do federal and state regulations interact?', '["State always supersedes federal", "Federal always supersedes state", "Stricter standard typically applies", "They never overlap"]', 2, 'Generally, the stricter standard applies whether federal or state, and organizations must comply with both.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Risk Assessment in Compliance'), 'What factors determine compliance risk priority?', '["Only likelihood of occurrence", "Only potential impact", "Likelihood and impact combined", "Regulatory preference"]', 2, 'Effective risk assessment considers both the likelihood of violations and their potential impact to prioritize appropriately.', 1),
((SELECT id FROM lessons WHERE title = 'Risk Assessment in Compliance'), 'How often should risk assessments be conducted?', '["Once at startup", "Only after incidents", "Regularly and after changes", "When auditors request"]', 2, 'Regular reassessment and evaluation after significant changes ensures risk management stays current.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Compliance Training Programs'), 'What makes compliance training most effective?', '["Annual long presentations", "Role-specific interactive scenarios", "Legal document reading", "Email reminders only"]', 1, 'Role-specific interactive training that uses real scenarios is most effective at driving behavioral change.', 1),
((SELECT id FROM lessons WHERE title = 'Compliance Training Programs'), 'How do you measure training effectiveness?', '["Completion rates only", "Quiz scores only", "Behavior change and incident reduction", "Employee feedback only"]', 2, 'True effectiveness is measured by actual behavior change and reduction in compliance incidents.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Audit Preparation and Response'), 'What is most important for audit readiness?', '["Expensive consultants", "Organized documentation", "Delaying the audit", "Minimal preparation"]', 1, 'Well-organized documentation demonstrating compliance activities is the foundation of successful audits.', 1),
((SELECT id FROM lessons WHERE title = 'Audit Preparation and Response'), 'How should you respond to audit findings?', '["Dispute everything", "Develop prompt corrective actions", "Ignore minor findings", "Wait for formal requirements"]', 1, 'Prompt development of corrective action plans demonstrates commitment to compliance and often reduces penalties.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Data Protection and Privacy'), 'What is the principle of data minimization?', '["Minimize storage costs", "Collect only necessary data", "Reduce employee access", "Minimize compliance effort"]', 1, 'Data minimization means collecting and retaining only data that is necessary for specified purposes.', 1),
((SELECT id FROM lessons WHERE title = 'Data Protection and Privacy'), 'What is privacy by design?', '["Designing secure facilities", "Building privacy into systems from start", "Graphic design for privacy policies", "Restricting design access"]', 1, 'Privacy by design means building privacy protection into systems and processes from the beginning, not adding it later.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Third-Party Vendor Compliance'), 'What is your compliance responsibility for vendors?', '["None, vendors are independent", "You remain ultimately responsible", "Only for contracted services", "Responsibility transfers to vendor"]', 1, 'Organizations remain responsible for vendor compliance with regulations, even when outsourcing services.', 1),
((SELECT id FROM lessons WHERE title = 'Third-Party Vendor Compliance'), 'When should vendor compliance be assessed?', '["Only after problems occur", "Before engagement and ongoing", "Annual reviews only", "When contracts renew"]', 1, 'Due diligence before engagement and ongoing monitoring throughout the relationship ensures continuous vendor compliance.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Incident Response and Reporting'), 'What is the first priority in incident response?', '["Determine blame", "Containment and mitigation", "Public relations", "Legal consultation"]', 1, 'Immediate containment prevents further harm and is always the first priority in incident response.', 1),
((SELECT id FROM lessons WHERE title = 'Incident Response and Reporting'), 'When must compliance incidents be reported?', '["Never, to protect reputation", "As required by applicable regulations", "Only if discovered by auditors", "At year-end reviews"]', 1, 'Regulations often specify strict timeframes for incident reporting, and failure to report can increase penalties.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Compliance Metrics and Reporting'), 'What makes compliance metrics valuable?', '["Complex statistical analysis", "Actionable insights for improvement", "Volume of data collected", "Impressive visualizations"]', 1, 'Valuable metrics provide actionable insights that enable continuous improvement of compliance programs.', 1),
((SELECT id FROM lessons WHERE title = 'Compliance Metrics and Reporting'), 'How should compliance be reported to executives?', '["Detailed technical analysis", "Legal jargon and citations", "Clear dashboards focused on material risks", "Only when problems occur"]', 2, 'Executive reporting should be clear, concise, and focused on material risks and actionable information.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Building a Compliance Culture'), 'What is most critical for compliance culture?', '["Strict policies", "Tone from leadership", "Punishment for violations", "Complex procedures"]', 1, 'Leadership tone and example are the most powerful drivers of organizational compliance culture.', 1),
((SELECT id FROM lessons WHERE title = 'Building a Compliance Culture'), 'How do you sustain compliance culture long-term?', '["Annual training only", "Integrate into daily operations and recognition", "Fear of punishment", "Quarterly memos"]', 1, 'Sustainable compliance culture requires integration into daily operations, performance management, and positive recognition.', 2);