INSERT INTO public.lessons (title, description, category, difficulty, duration_minutes, content, scenario_text, is_published, order_index) VALUES
('Enterprise AI Governance Frameworks', 'Establish comprehensive governance structures for responsible and effective AI deployment across your organization.', 'ai_literacy', 'advanced', 5, '{"sections": [{"title": "Governance Structure", "bullets": ["Define roles and responsibilities", "Establish AI oversight committees", "Create decision-making frameworks", "Set approval processes for AI initiatives"], "content": "Effective governance ensures AI aligns with organizational values and objectives."}, {"title": "Policy Components", "bullets": ["Ethics and responsible AI guidelines", "Data usage and privacy standards", "Risk assessment procedures", "Compliance and audit requirements"], "content": "Comprehensive policies provide guardrails for AI innovation."}]}', 'Your organization is deploying AI across multiple departments with no central oversight. How do you establish governance?', true, 37),
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Enterprise AI Governance Frameworks'), 'What is the primary purpose of AI governance?', '["Slow down AI adoption", "Align AI with organizational values and objectives", "Reduce AI spending", "Eliminate all AI risks"]', 1, 'Governance ensures AI initiatives align with organizational values and strategic objectives while managing risks appropriately.', 1),
((SELECT id FROM lessons WHERE title = 'Enterprise AI Governance Frameworks'), 'Who should be involved in AI governance oversight?', '["Only IT department", "Only legal and compliance", "Cross-functional stakeholders including business, legal, IT, and ethics", "External consultants only"]', 2, 'Effective AI governance requires diverse perspectives from business, legal, technical, and ethical domains to address all dimensions of AI deployment.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'AI Strategy and Roadmap Development'), 'What should drive AI strategy prioritization?', '["Latest AI trends", "Business value and organizational readiness", "Competitor actions only", "Technology capabilities alone"]', 1, 'Successful AI strategy balances business value potential with organizational readiness to ensure initiatives deliver meaningful impact.', 1),
((SELECT id FROM lessons WHERE title = 'AI Strategy and Roadmap Development'), 'How should AI roadmaps handle uncertainty?', '["Commit to fixed 5-year plans", "Build in learning cycles and flexibility", "Avoid planning beyond next quarter", "Copy competitor roadmaps"]', 1, 'AI roadmaps should provide direction while incorporating learning cycles and flexibility to adapt as technology and understanding evolve.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'AI Risk Management at Enterprise Scale'), 'What type of AI risk is most difficult to detect?', '["Technical errors", "Integration failures", "Subtle bias in decision-making", "Cost overruns"]', 2, 'Subtle bias in AI decision-making can be difficult to detect yet cause significant harm, requiring systematic monitoring and diverse testing.', 1),
((SELECT id FROM lessons WHERE title = 'AI Risk Management at Enterprise Scale'), 'When should AI risk assessment occur?', '["Only after deployment", "Only during development", "Continuously throughout AI lifecycle", "Only when incidents occur"]', 2, 'Continuous risk assessment throughout the AI lifecycle enables early detection and mitigation before issues become critical.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'AI Procurement and Vendor Management'), 'What is a critical AI vendor contract consideration?', '["Lowest price", "Data ownership and usage rights", "Vendor office location", "Number of features"]', 1, 'Data ownership and usage rights are critical because they determine control over your data and ability to switch vendors or use data elsewhere.', 1),
((SELECT id FROM lessons WHERE title = 'AI Procurement and Vendor Management'), 'How should you evaluate AI vendor claims?', '["Trust marketing materials", "Require proof of performance and test with your data", "Check social media reviews only", "Base decision on vendor size"]', 1, 'Vendor claims should be validated through proof of performance and testing with your actual data to ensure the solution meets your needs.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Building AI Centers of Excellence'), 'What is a primary benefit of an AI Center of Excellence?', '["Centralized control over all AI decisions", "Accelerated adoption with consistent standards", "Reduced need for business unit involvement", "Elimination of all AI risks"]', 1, 'COEs accelerate AI adoption by providing expertise, standards, and support while maintaining quality and governance across the organization.', 1),
((SELECT id FROM lessons WHERE title = 'Building AI Centers of Excellence'), 'How should a COE balance centralization and business unit autonomy?', '["Full central control of all AI", "Enable business units with standards and support", "Complete business unit independence", "Rotate control quarterly"]', 1, 'Effective COEs provide standards, best practices, and support while enabling business units to innovate within appropriate guardrails.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'AI Policy and Regulatory Compliance'), 'How do you stay current with evolving AI regulations?', '["Wait for legal violations", "Proactive monitoring and policy updates", "Ignore until enforced", "Copy competitor policies"]', 1, 'Proactive monitoring of regulatory developments and regular policy updates ensure compliance before enforcement actions occur.', 1),
((SELECT id FROM lessons WHERE title = 'AI Policy and Regulatory Compliance'), 'What is essential for demonstrating AI compliance?', '["Verbal assurances", "Comprehensive documentation and audit trails", "Insurance policies", "External certifications only"]', 1, 'Comprehensive documentation and audit trails provide evidence of compliance and enable effective responses to regulatory inquiries.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'AI Performance Monitoring and ROI Measurement'), 'What should AI performance metrics include?', '["Only technical accuracy", "Only cost savings", "Technical performance and business outcomes", "User satisfaction only"]', 2, 'Comprehensive metrics combine technical performance with business outcomes to provide a complete picture of AI value and effectiveness.', 1),
((SELECT id FROM lessons WHERE title = 'AI Performance Monitoring and ROI Measurement'), 'When should you measure AI ROI?', '["Only at project end", "Never, ROI is too complex", "Continuously from baseline through deployment", "Only when requested by finance"]', 2, 'Continuous ROI measurement from baseline establishment through deployment and operation enables ongoing optimization and value demonstration.', 2);