INSERT INTO public.lessons (title, description, category, difficulty, duration_minutes, content, scenario_text, is_published, order_index) VALUES
('Board-Level Compliance Reporting', 'Master the art of communicating compliance status, risks, and strategy to board members effectively.', 'compliance_workflows', 'advanced', 5, '{"sections": [{"title": "Board Communication Essentials", "bullets": ["Focus on material risks and strategic implications", "Use clear metrics and trend analysis", "Provide context and business impact", "Balance transparency with actionable insights"], "content": "Boards need compliance information that enables informed governance decisions."}, {"title": "Reporting Framework", "bullets": ["Executive summary with key takeaways", "Risk heat maps and trend indicators", "Regulatory changes impacting strategy", "Resource needs and recommendations"], "content": "Structured reporting ensures boards receive critical information efficiently."}]}', 'The board requests a compliance briefing for their next meeting. How do you structure a report that demonstrates value while addressing concerns?', true, 44),
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Board-Level Compliance Reporting'), 'What should board compliance reports prioritize?', '["Detailed policy documentation", "Material risks and strategic implications", "All compliance activities", "Technical compliance metrics"]', 1, 'Boards need to focus on material risks and strategic implications rather than operational details to make informed governance decisions.', 1),
((SELECT id FROM lessons WHERE title = 'Board-Level Compliance Reporting'), 'How should compliance trends be presented to the board?', '["Raw data tables", "Heat maps and visual indicators with context", "Lengthy written reports", "Verbal summaries only"]', 1, 'Visual indicators like heat maps combined with business context enable boards to quickly understand compliance status and trends.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Strategic Compliance Planning'), 'What timeframe should strategic compliance planning cover?', '["Next quarter only", "3-5 years aligned with business strategy", "Current regulatory requirements only", "Until next audit"]', 1, 'Strategic compliance planning should align with business strategy horizons, typically 3-5 years, to anticipate and prepare for regulatory evolution.', 1),
((SELECT id FROM lessons WHERE title = 'Strategic Compliance Planning'), 'How can compliance provide competitive advantage?', '["By being minimally compliant", "By enabling trusted operations and market differentiation", "By avoiding all regulations", "By copying competitors"]', 1, 'Excellence in compliance can differentiate organizations, build stakeholder trust, and enable business opportunities in regulated markets.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Compliance Program Maturity Assessment'), 'What characterizes an optimized compliance program?', '["Documented policies", "Ad hoc responses", "Integrated and continuously improving", "Outsourced completely"]', 2, 'Optimized programs are integrated across the organization and continuously improve through systematic measurement and refinement.', 1),
((SELECT id FROM lessons WHERE title = 'Compliance Program Maturity Assessment'), 'How should compliance maturity improvements be prioritized?', '["By regulatory mandate", "By high-impact gaps and business value", "By ease of implementation", "By budget availability"]', 1, 'Prioritizing high-impact gaps that deliver business value ensures maturity investments produce meaningful risk reduction and efficiency.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Compliance Crisis Leadership'), 'What is the first leadership priority in a compliance crisis?', '["Assign blame", "Establish crisis command and coordinate response", "Prepare public statement", "Consult with insurers"]', 1, 'Establishing clear crisis command structure and coordinating response across functions is essential for effective crisis management.', 1),
((SELECT id FROM lessons WHERE title = 'Compliance Crisis Leadership'), 'How should leaders approach post-crisis improvement?', '["Move on quickly", "Conduct root cause analysis and implement systemic changes", "Blame individuals", "Wait for regulatory guidance"]', 1, 'Thorough root cause analysis and systemic improvements transform crises into opportunities for strengthening organizational resilience.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Compliance Investment and Resource Strategy'), 'How should compliance investments be justified?', '["As pure cost center", "By quantifying risk reduction and business enablement", "By regulatory requirement alone", "By competitor spending"]', 1, 'Quantifying both risk reduction and business enablement value creates compelling business cases for compliance investments.', 1),
((SELECT id FROM lessons WHERE title = 'Compliance Investment and Resource Strategy'), 'What drives efficient compliance resource allocation?', '["Equal distribution across all areas", "Risk-based prioritization and technology leverage", "Historical spending patterns", "Regulatory preferences"]', 1, 'Risk-based prioritization combined with technology and automation delivers maximum compliance effectiveness per dollar invested.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Integrating Compliance with Business Strategy'), 'When should compliance be involved in strategic planning?', '["After decisions are made", "At the beginning of strategic planning", "Only for regulated industries", "When legal requires it"]', 1, 'Early compliance involvement in strategic planning ensures regulatory considerations inform rather than derail business initiatives.', 1),
((SELECT id FROM lessons WHERE title = 'Integrating Compliance with Business Strategy'), 'How should compliance be positioned strategically?', '["As cost center", "As business enabler and competitive advantage", "As necessary evil", "As legal function only"]', 1, 'Positioning compliance as a business enabler and potential competitive advantage drives integration and organizational support.', 2);
INSERT INTO public.quiz_questions (lesson_id, question_text, options, correct_answer, explanation, order_index) VALUES
((SELECT id FROM lessons WHERE title = 'Board Compliance Committee Effectiveness'), 'What distinguishes an effective compliance committee?', '["Review of all compliance details", "Focus on strategic and material compliance issues", "Monthly multi-hour meetings", "Micromanagement of compliance staff"]', 1, 'Effective committees focus on strategic and material issues rather than operational details, enabling meaningful oversight without micromanaging.', 1),
((SELECT id FROM lessons WHERE title = 'Board Compliance Committee Effectiveness'), 'How should compliance committee expertise be ensured?', '["Any board member can serve", "Members with relevant regulatory and industry expertise", "Rotate all members annually", "Limit to financial experts only"]', 1, 'Committee members need relevant regulatory and industry expertise to provide informed oversight and valuable guidance to management.', 2);